mapper.update:
update wallet
set balance=balance-#{amount}
where photographer_id=#{photographerId}
and balance >= #{amount}

update wallet
set balance=balance+#{amount}
where user_id=#{userId}

update wallet
set frozen_amount=#{frozeAmount}
where user_id=#{userId}

update wallet
set frozen_amount-=#{frozenAmount}
where user_id=#{userId}
and frozen_amount >=#{frozenAmount}

update wallet
set frozen_amount-=#{frozenAmount} ,
balance+=#{frozenAmount}
where user_id=#{userId}
and frozen_amount >=#{frozenAmount}
